package org.jugru.jdbcExample.util;import org.h2.tools.Server;import org.jugru.jdbcExample.util.exceptions.DBException;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public class DBManager {    private static Server server = null;    public static void start() {        try {            if(server == null) {                server = Server.createWebServer( "-web", "-webAllowOthers", "-webPort", "8084");            }            server.start();        } catch (SQLException e) {            e.printStackTrace();            throw new DBException(e.getMessage());        }    }    public static void stop() {        if(server != null) {            server.stop();        }    }    public static Connection createNewConnection() {        try {            return DriverManager.getConnection("jdbc:h2:mem:./data/testDB;IFEXISTS=FALSE;INIT=create schema if not exists testDB", "sa", "");        } catch (SQLException e) {            e.printStackTrace();            throw new DBException(e.getMessage());        }    }}